import React from 'react';

import Task from './Task';

class TaskList extends React.Component {

  constructor(props) {
    super(props);
    this.state = {total_tasks: 0, tasks: [], card_id: this.props.card_id};

    this.addNewTask = this.addNewTask.bind(this);
    this.deleteTask = this.deleteTask.bind(this);
    this.deleteCard = this.deleteCard.bind(this);
  }

  deleteTask(task_id) {
    this.setState(function(state) {
      delete state.tasks[task_id];
      return {
        tasks: state.tasks
      };
    });
  }

  deleteCard() {
    this.props.deleteCard(this.state.card_id);
  }

  addNewTask() {
    this.setState(function(state) {
      let r = state.tasks;
      r.push(<Task 
                task_id={state.tasks.length} 
                deleteTask={this.deleteTask} 
                />)
      return {
        tasks: r
      };
    });
  }

  render () {
    return (
      <div>
        <button onClick={this.addNewTask}>Add new task</button>
        <button onClick={this.deleteCard}>Delete card</button>
        <text>New tasks: {this.state.tasks.length}</text>
        <div>{this.state.tasks}</div>
        <br /> 
        <br />
      </div>
    );
  }
}

export default TaskList;
